import { Component, OnInit, ViewChild, ElementRef } from '@angular/core';
import { UntypedFormGroup, UntypedFormBuilder } from '@angular/forms';
import { ApiService } from '../shared/api.service';
import { RestaurentData } from './restaurent.model';

@Component({
  selector: 'app-restaurent-dash',
  templateUrl: './restaurent-dash.component.html',
  styleUrls: ['./restaurent-dash.component.css']
})
export class RestaurentDashComponent implements OnInit {
  formValue!: UntypedFormGroup;
  restaurentModelObj: RestaurentData = new RestaurentData();
  allRestaurentData: any;
  filteredRestaurentData: any;
  showAdd!: boolean;
  showBtn!: boolean;
  searchTerm: string = '';

  @ViewChild('closeButton') closeButton!: ElementRef;

  constructor(private formbuilder: UntypedFormBuilder, private api: ApiService) { }

  ngOnInit(): void {
    this.formValue = this.formbuilder.group({
      name: [''],
      email: [''],
      mobile: [''],
      address: [''],
      services: [''],
    });
    this.getAllData();
  }

  clickAddResto() {
    this.formValue.reset();
    this.showAdd = true;
    this.showBtn = false;
  }

  addRestaurent() {
    this.restaurentModelObj = { ...this.formValue.value };

    this.api.postRestaurent(this.restaurentModelObj).subscribe(res => {
      alert("Restaurant Added Successfully");
      this.formValue.reset();
      this.closeButton.nativeElement.click(); // Close modal
      this.getAllData();
    }, err => {
      console.log(err);
      alert("Failed to add restaurant!");
    });
  }

  getAllData() {
    this.api.getRestaurent().subscribe(res => {
      this.allRestaurentData = res;
      this.filteredRestaurentData = res;
    }, err => {
      console.log(err);
    });
  }

  deleteResto(id: any) {
    this.api.deleteRestaurant(id).subscribe(res => {
      alert("Restaurant Deleted Successfully");
      this.getAllData();
    }, err => {
      console.log(err);
    });
  }

  onEditResto(data: any) {
    this.showAdd = false;
    this.showBtn = true;

    this.restaurentModelObj.id = data.id;
    this.formValue.patchValue(data);
  }

  updateResto() {
    this.restaurentModelObj = { ...this.restaurentModelObj, ...this.formValue.value };

    this.api.updateRestaurant(this.restaurentModelObj.id, this.restaurentModelObj).subscribe(res => {
      alert("Restaurant Updated Successfully");
      this.formValue.reset();
      this.closeButton.nativeElement.click(); // Close modal
      this.getAllData();
    }, err => {
      console.log(err);
    });
  }

  filterData() {
    this.filteredRestaurentData = this.allRestaurentData.filter((restaurant: any) =>
      restaurant.name.toLowerCase().includes(this.searchTerm.toLowerCase())
    );
  }
}
<nav class="navbar navbar-light bg-primary"> 
  <div class="container-fluid">
    <h2>Restaurant Record App</h2>
    <div class="d-flex">
      <button (click)="clickAddResto()" class="btn btn-success" type="button" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Add Restaurant
      </button>
      <button routerLink="/login" style="margin-left: 5px;" class="btn btn-danger" type="button">Log Out</button>
    </div>
  </div>
</nav>

<div class="p-5 mb-4 bg-light rounded-3">
  <div class="container">
    <h1 class="display-5 fw-bold">Restaurant Records Management System</h1>
    <p class="col-md-8 fs-4">You can save records of restaurants here.</p>
    <input type="text" class="form-control mt-3" placeholder="Search by Restaurant Name" [(ngModel)]="searchTerm" (input)="filterData()" />
  </div>
</div>

<table class="table mt-4">
  <thead>
    <tr>
      <th scope="col">Restaurant Id</th>
      <th scope="col">Restaurant Name</th>
      <th scope="col">Restaurant Email</th> 
      <th scope="col">Restaurant Address</th> 
      <th scope="col">Restaurant Phone</th>
      <th scope="col">Restaurant Services</th>
      <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let data of filteredRestaurentData">
      <td>{{ data.id }}</td>
      <td>{{ data.name }}</td>
      <td>{{ data.email }}</td>
      <td>{{ data.address }}</td>
      <td>{{ data.mobile }}</td>
      <td>{{ data.services }}</td>
      <td>
        <button (click)="onEditResto(data)" class="btn btn-info" type="button" data-bs-toggle="modal" data-bs-target="#exampleModal">
          Edit
        </button>
        &nbsp;
        <button (click)="deleteResto(data.id)" class="btn btn-danger">Delete</button>
      </td>
    </tr>
  </tbody>
</table>




<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Records</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form [formGroup]="formValue">
          <div class="mb-3">
            <label class="form-label">Name</label>
            <input type="text" class="form-control" formControlName="name" placeholder="Restaurant Name">
          </div>
          <div class="mb-3">
            <label class="form-label">Email</label>
            <input type="email" class="form-control" formControlName="email" placeholder="abcd@gmail.com">
          </div>
          <div class="mb-3">
            <label class="form-label">Address</label>
            <input type="text" class="form-control" formControlName="address" placeholder="Address details">
          </div>
          <div class="mb-3">
            <label class="form-label">Mobile</label>
            <input type="number" class="form-control" formControlName="mobile" placeholder="+91-1234567890">
          </div>
          <div class="mb-3">
            <label class="form-label">Services</label>
            <input type="text" class="form-control" formControlName="services" placeholder="e.g., coffee, tea">
          </div>
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal" #closeButton>Close</button>
        <button *ngIf="showAdd" type="button" class="btn btn-primary" (click)="addRestaurent()">Add Details</button>
        <button *ngIf="showBtn" type="button" class="btn btn-primary" (click)="updateResto()">Update</button>
      </div>
    </div>
  </div>
</div>
