import { Component, OnInit, ViewChild, ElementRef } from '@angular/core';
import { UntypedFormGroup, UntypedFormBuilder } from '@angular/forms';
import { ApiService } from '../shared/api.service';
import { RestaurentData } from './restaurent.model';

@Component({
  selector: 'app-restaurent-dash',
  templateUrl: './restaurent-dash.component.html',
  styleUrls: ['./restaurent-dash.component.css']
})
export class RestaurentDashComponent implements OnInit {
  formValue!: UntypedFormGroup;
  restaurentModelObj: RestaurentData = new RestaurentData();
  allRestaurentData: any;
  showAdd!: boolean;
  showBtn!: boolean;

  @ViewChild('closeButton') closeButton!: ElementRef;

  constructor(private formbuilder: UntypedFormBuilder, private api: ApiService) { }

  ngOnInit(): void {
    this.formValue = this.formbuilder.group({
      name: [''],
      email: [''],
      mobile: [''],
      address: [''],
      services: [''],
    });
    this.getAllData();
  }

  clickAddResto() {
    this.formValue.reset();
    this.showAdd = true;
    this.showBtn = false;
  }

  addRestaurent() {
    this.restaurentModelObj = { ...this.formValue.value };

    this.api.postRestaurent(this.restaurentModelObj).subscribe(res => {
      alert("Restaurant Added Successfully");
      this.formValue.reset();
      this.closeButton.nativeElement.click(); // Close modal
      this.getAllData();
    }, err => {
      console.log(err);
      alert("Failed to add restaurant!");
    });
  }

  getAllData() {
    this.api.getRestaurent().subscribe(res => {
      this.allRestaurentData = res;
    }, err => {
      console.log(err);
    });
  }

  deleteResto(id: any) {
    this.api.deleteRestaurant(id).subscribe(res => {
      alert("Restaurant Deleted Successfully");
      this.getAllData();
    }, err => {
      console.log(err);
    });
  }

  onEditResto(data: any) {
    this.showAdd = false;
    this.showBtn = true;

    this.restaurentModelObj.id = data.id;
    this.formValue.patchValue(data);
  }

  updateResto() {
    this.restaurentModelObj = { ...this.restaurentModelObj, ...this.formValue.value };

    this.api.updateRestaurant(this.restaurentModelObj.id, this.restaurentModelObj).subscribe(res => {
      alert("Restaurant Updated Successfully");
      this.formValue.reset();
      this.closeButton.nativeElement.click(); // Close modal
      this.getAllData();
    }, err => {
      console.log(err);
    });
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Restaurant Record App</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
  <link rel="restaurant-dash.component" href="styles.css">
</head>
<body>
  <nav class="navbar navbar-expand-lg navbar-dark bg-dark p-3 shadow">
    <div class="container-fluid">
      <h2 class="navbar-brand">Restaurant Record App</h2>
      <div class="d-flex">
        <button (click)="clickAddResto()" class="btn btn-accent me-2" type="button" data-bs-toggle="modal" data-bs-target="#exampleModal">
          Add Restaurant
        </button>
        <button routerLink="/login" class="btn btn-secondary" type="button">Log Out</button>
      </div>
    </div>
  </nav>

  <div class="p-5 bg-soft rounded-3 mt-4 shadow-sm text-center">
    <h1 class="display-5 fw-bold text-accent">Restaurant Records Management System</h1>
    <p class="fs-4 text-muted">Save and manage restaurant records effortlessly.</p>
  </div>

  <div class="container mt-4">
    <table class="table table-hover align-middle shadow-sm">
      <thead class="table-dark text-center">
        <tr>
          <th scope="col">Restaurant Id</th>
          <th scope="col">Name</th>
          <th scope="col">Email</th>
          <th scope="col">Address</th>
          <th scope="col">Phone</th>
          <th scope="col">Services</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
      <tbody class="text-center text-muted">
        <tr *ngFor="let data of allRestaurentData">
          <td>{{ data.id }}</td>
          <td>{{ data.name }}</td>
          <td>{{ data.email }}</td>
          <td>{{ data.address }}</td>
          <td>{{ data.mobile }}</td>
          <td>{{ data.services }}</td>
          <td>
            <button (click)="onEditResto(data)" class="btn btn-info btn-sm me-2" type="button" data-bs-toggle="modal" data-bs-target="#exampleModal">
              Edit
            </button>
            <button (click)="deleteResto(data.id)" class="btn btn-danger btn-sm">Delete</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>

  <!-- Modal -->
  <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header bg-dark text-light">
          <h5 class="modal-title" id="exampleModalLabel">Restaurant Details</h5>
          <button type="button" class="btn-close btn-light" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
        <div class="modal-body">
          <form [formGroup]="formValue">
            <div class="mb-3">
              <label class="form-label">Name</label>
              <input type="text" class="form-control" formControlName="name" placeholder="Restaurant Name">
            </div>
            <div class="mb-3">
              <label class="form-label">Email</label>
              <input type="email" class="form-control" formControlName="email" placeholder="example@gmail.com">
            </div>
            <div class="mb-3">
              <label class="form-label">Address</label>
              <input type="text" class="form-control" formControlName="address" placeholder="Address">
            </div>
            <div class="mb-3">
              <label class="form-label">Phone</label>
              <input type="number" class="form-control" formControlName="mobile" placeholder="+91-1234567890">
            </div>
            <div class="mb-3">
              <label class="form-label">Services</label>
              <input type="text" class="form-control" formControlName="services" placeholder="e.g., coffee, tea">
            </div>
          </form>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-outline-secondary" data-bs-dismiss="modal">Close</button>
          <button *ngIf="showAdd" type="button" class="btn btn-primary" (click)="addRestaurent()">Add Details</button>
          <button *ngIf="showBtn" type="button" class="btn btn-primary" (click)="updateResto()">Update</button>
        </div>
      </div>
    </div>
  </div>

  <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>
</body>
</html>
